	function fadeIn() {
		for (var i = 0; i < leftInDivs.length; i++) {
			// @ts-ignore
			var leftIn = leftInDivs[i];
			var rightIn = rightInDivs[i];

			var distInView = leftIn.getBoundingClientRect().top - window.innerHeight + 20;
			if (distInView < 0) {
				try {
					leftIn.classList.add('in-animate');
					rightIn.classList.add('in-animate');
				} catch (err) {}
			} else {
				try {
					leftIn.classList.remove('in-animate');
					rightIn.classList.remove('in-animate');
				} catch (err) {}
			}
		}

		for (var i = 0; i < topIn.length; i++) {
	
